'use strict';

import fs from 'fs';
import path from 'path';
import Sequelize from 'sequelize';
const basename = path.basename(__filename);
const config = require('../../config/config.js');
const env = process.env.NODE_ENV;
const db = {};
let sequelize;
if (config[env].url) {
  sequelize = new Sequelize(config[env].url);
} else {
  sequelize = new Sequelize(process.env.DEV_DATABASE_URL);
}

fs.readdirSync(__dirname)
  .filter((file) => {
    return (
      file.indexOf('.') !== 0 && file !== basename && file.slice(-3) === '.js'
    );
  })
  .forEach((file) => {
    const model = require(path.join(__dirname, file))(
      sequelize,
      Sequelize.DataTypes
    );
    db[model.name] = model;
  });

Object.keys(db).forEach((modelName) => {
  if (db[modelName].associate) {
    db[modelName].associate(db);
  }
});
export const dbConnection=sequelize.authenticate().then(
  ()=>console.log(env,'database connection established')
).catch(err=>console.log('unable to connect to the database',err))
db.sequelize = sequelize;
db.Sequelize = Sequelize;

module.exports = db;

require('dotenv').config()
module.exports = {
  development: {
    url: process.env.DEV_DATABASE_URL,
    dialect: 'postgres',
  },
  test: {
    url: process.env.TEST_DATABASE_URL,
    dialect: 'postgres',
  },
  production: {
    url: process.env.DATABASE_URL,
    dialect: 'postgres',
  },
}

'use strict';
const {
  Model
} = require('sequelize');
module.exports = (sequelize, DataTypes) => {
  class Category extends Model {
    /**
     * Helper method for defining associations.
     * This method is not a part of Sequelize lifecycle.
     * The `models/index` file will call this method automatically.
     */
    static associate(models) {
      // define association here
      this.hasMany(models.Product, { foreignKey: 'categoryId'})
    }
  }
  Category.init({
    name: DataTypes.STRING,
    status: DataTypes.STRING
  }, {
    sequelize,
    modelName: 'Category',
    tableName: 'Categories'
  });
  return Category;
};

'use strict';
const {
  Model
} = require('sequelize');
module.exports = (sequelize, DataTypes) => {
  class Coupon extends Model {
    /**
     * Helper method for defining associations.
     * This method is not a part of Sequelize lifecycle.
     * The `models/index` file will call this method automatically.
     */
    static associate(models) {
      // define association here
      this.belongsTo(models.user, { foreignKey: 'vendorId' })
      this.hasMany(models.CouponProduct, { foreignKey: 'couponId' })
    }
  }
  Coupon.init({
    code: DataTypes.STRING,
    discount_rate: DataTypes.INTEGER,
    expire_at: DataTypes.DATE,
    max_usage: DataTypes.INTEGER,
    usage: DataTypes.INTEGER,
    status: DataTypes.STRING,
    vendorId: DataTypes.UUID,
  }, {
    sequelize,
    modelName: 'Coupon',
  });
  return Coupon;
};

