        counter2++;
        if (counter2 === ordersList.products.length) {
            db.Order.findOne({
              where: { userId: buyerId, status: 'paid' },
            }).then(async (order) => {
              order.status = 'shipping';
              return order.save();
            });
          emitter.emit('productPurchased', invoice);
          res.status(200).json({
